def deepest_nested_parentheses(string):
    max_depth = 0
    current_depth = 0
    for char in string:
        if char == '(':
            current_depth += 1
            if current_depth > max_depth:
                max_depth = current_depth
        elif char == ')':
            if current_depth > 0:
                current_depth -= 1
            else:
                return -1  # Invalid parentheses
    if current_depth > 0:
        return -1  # Invalid parentheses
    return max_depth
