import re

def base_url(url):
    """ Returns the base url given a full url
    """
    # Use regex to extract the base url
    base_url_match = re.match(r'(https?://[^/]+)', url)

    # Check if a match is found
    if base_url_match:
        # Return the base url as a string
        return base_url_match.group(1)
    else:
        return ""
