def asc_desc_ending_sort(n, m, strings):
    def compare_strings(a, b):
        for i in range(m):
            if i % 2 == 0:
                if a[i] < b[i]:
                    return -1
                elif a[i] > b[i]:
                    return 1
            else:
                if a[i] > b[i]:
                    return -1
                elif a[i] < b[i]:
                    return 1
        return 0

    indices = list(range(1, n+1))
    indices.sort(key=lambda i: strings[i-1])

    indices.sort(key=lambda i: strings[i-1], cmp=compare_strings)

    return indices
