def compare(s1, s2):
    for i in range(len(s1)):
        if i % 2 == 0:  # even position
            if s1[i] < s2[i]:
                return -1
            elif s1[i] > s2[i]:
                return 1
        else:  # odd position
            if s1[i] > s2[i]:
                return -1
            elif s1[i] < s2[i]:
                return 1
    return 0

def asc_desc_ending_sort(n, m, strings):
    indices = [i for i in range(1, n+1)]
    indices.sort(key=lambda i: strings[i-1])

    return sorted(indices, key=lambda i: strings[i-1], cmp=compare, reverse=True)
