n, m = map(int, input().split())
books = [input() for _ in range(n)]

def compare(a, b):
    for i in range(m):
        if i % 2 == 0:
            if a[i] < b[i]:
                return -1
            elif a[i] > b[i]:
                return 1
        else:
            if a[i] > b[i]:
                return -1
            elif a[i] < b[i]:
                return 1
    return 0

sorted_indices = sorted(range(n), key=lambda x: books[x], cmp=compare)
result = ' '.join([str(i+1) for i in sorted_indices])
