import math

def count_permutations(n, m, k, p):
    def calculate_factorial(n):
        factorial = 1
        for i in range(2, n+1):
            factorial *= i
        return factorial

    def calculate_combinations(n, k):
        return calculate_factorial(n) // (calculate_factorial(k) * calculate_factorial(n-k))

    def calculate_power(base, exponent, modulus):
        result = 1
        while exponent > 0:
            if exponent % 2 == 1:
                result *= base
                result %= modulus
            base *= base
            base %= modulus
            exponent //= 2
        return result

    count = 0
    for x in range(1, n+1):
        combinations = calculate_combinations(n-1, m-1)
        power = calculate_power(m-1, k-1, p)
        count += combinations * power
        count %= p

    return count

n, m, k, p = map(int, input().split())
count_permutations(n, m, k, p)
