from itertools import combinations
def minimal_difference(a, b, c):
    songs = [1] * a + [2] * b + [3] * c
    min_diff = float('inf')
    for i in range(1, len(songs) // 2 + 1):
        for comb in combinations(songs, i):
            duration_1 = sum(comb)
            duration_2 = sum(songs) - duration_1
            min_diff = min(min_diff, abs(duration_1 - duration_2))
    return min_diff
