def minimal_difference(a, b, c):
    total_duration = a + 2*b + 3*c
    target_duration = total_duration // 2
    dp = [False] * (target_duration + 1)
    dp[0] = True
    for i in range(a+1):
        for j in range(target_duration, i-1, -1):
            dp[j] |= dp[j-i]
    for i in range(b+1):
        for j in range(target_duration, i*2-1, -1):
            dp[j] |= dp[j-i*2]
    for i in range(c+1):
        for j in range(target_duration, i*3-1, -1):
            dp[j] |= dp[j-i*3]
    for i in range(target_duration, -1, -1):
        if dp[i]:
            return total_duration - i*2
    return -1

assert minimal_difference(1, 1, 1) == 0
assert minimal_difference(2, 1, 3) == 1
