def find_min_diff(a, b, c):
    total_duration = a + 2 * b + 3 * c
    target_duration = total_duration // 2

    # Use dynamic programming to find the minimum difference
    dp = [[False] * (target_duration + 1) for _ in range(total_duration + 1)]
    dp[0][0] = True

    for i in range(1, total_duration + 1):
        for j in range(target_duration + 1):
            if j - i >= 0 and dp[i - 1][j - i]:
                dp[i][j] = True
            if dp[i - 1][j]:
                dp[i][j] = True

    min_diff = float('inf')

    for j in range(target_duration, -1, -1):
        if dp[total_duration][j]:
            min_diff = total_duration - 2 * j
            break

    return min_diff
