def min_difference(a, b, c):
    total_duration = a + 2*b + 3*c

    half_duration = total_duration // 2

    # Dynamically fill up a table to keep track of the possible sums
    table = [[False] * (half_duration+1) for _ in range(4)]

    table[0][0] = True

    for i in range(1, 4):
        for j in range(half_duration+1):
            if j >= i:
                table[i][j] = table[i-1][j-i] or table[i-1][j]

    # Find the maximum possible sum closest to half_duration
    sum1 = half_duration
    while not table[3][sum1]:
        sum1 -= 1

    difference = total_duration - 2*sum1
    return difference
