def find_x(arr):
    arr = list(map(int, arr))
    n = len(arr)
    max_val = max(arr)

    dp = [set() for _ in range(max_val+1)]

    # base case, no element in subsequence
    for i in range(n):
        dp[arr[i]].add(0)

    for i in range(1, n):
        for j in range(i):
            if arr[j] < arr[i]:
                dx = arr[j] ^ arr[i]
                dp[arr[i]].add(dx)
                for val in dp[arr[j]]:
                    dp[arr[i]].add(val ^ dx)

    result = sorted(dp[max_val])
    return result

