def winner_of_game(arr):
    n = len(arr)
    dp = [[0] * n for _ in range(n)]

    for i in range(n):
        dp[i][i] = True

    for length in range(2, n + 1):
        for i in range(n - length + 1):
            j = i + length - 1
            if length == 2:
                dp[i][j] = arr[i] < arr[j]
            else:
                dp[i][j] = (arr[i] < arr[j] and not dp[i][j - 1]) or (arr[i] < arr[j] and not dp[i + 1][j])

    return 'Alice' if dp[0][n - 1] else 'Bob'
