def winner_game(A):
    n = len(A)
    dp = [[0] * n for _ in range(n)]

    for i in range(n):
        dp[i][i] = 1

    for length in range(2, n + 1):
        for i in range(n - length + 1):
            j = i + length - 1

            if length == 2 and A[i] < A[j]:
                dp[i][j] = 2
            elif A[i] < A[i + 1] and dp[i + 1][j] == 2:
                dp[i][j] = 1
            elif A[j] > A[j - 1] and dp[i][j - 1] == 2:
                dp[i][j] = 1
            else:
                dp[i][j] = 2

    if dp[0][n - 1] == 1:
        return 'Alice'
    else:
        return 'Bob'
