from typing import List

def find_smallest_d(arr: List[int]) -> int:
    for d in range(1, max(arr)+1):
        colors = [0] * len(arr)
        colors[0] = 1 if arr[0] % d == 0 else 2
        for i in range(1, len(arr)):
            if arr[i] % d == 0:
                colors[i] = 1
            else:
                colors[i] = 2
            if colors[i] == colors[i-1]:
                break
        else:
            return d
    return 0
