def is_valid_date(date_string):
    try:
        month, day, year = date_string.split("-")
        month = int(month)
        day = int(day)
        year = int(year)
        if month < 1 or month > 12:
            return False
        elif day < 1 or day > 31:
            return False
        elif month == 2:
            if year % 4 == 0 and (year % 100 != 0 or year % 400 == 0):
                return day <= 29
            else:
                return day <= 28
        elif month in [4, 6, 9, 11]:
            return day <= 30
        else:
            return True
    except ValueError:
        return False
