import re

def is_valid_date(date_string):
    pattern = r'^\d{2}-\d{2}-\d{4}$'
    if not re.match(pattern, date_string):
        return False

    month, day, year = map(int, date_string.split('-'))

    if month < 1 or month > 12:
        return False

    if day < 1 or day > 31:
        return False

    if month in [4, 6, 9, 11] and day > 30:
        return False

    if month == 2:
        if year % 400 == 0:
            if day > 29:
                return False
        elif year % 100 == 0:
            if day > 28:
                return False
        elif year % 4 == 0:
            if day > 29:
                return False
        else:
            if day > 28:
                return False

    return True
