import ast
import operator

def build_and_calculate(numbers, operations):
    numbers_list = ast.literal_eval(numbers)
    operators = {'+': operator.add, '-': operator.sub, '*': operator.mul, '/': operator.truediv, '**': operator.pow}

    result = numbers_list[0]
    for i in range(1, len(numbers_list)):
        operation = operators[operations[i-1]]
        result = operation(result, numbers_list[i])

    return result
