def fibonacci(n):
    if n <= 0:
        return 0
    elif n == 1:
        return 1
    else:
        fib1 = 0
        fib2 = 1
        for i in range(2, n + 1):
            fib = fib1 + fib2
            fib1 = fib2
            fib2 = fib
        return fib


