def caesar_cipher(str, shift):
    encrypted_str = ""
    for char in str:
        if char.isalpha():
            ascii_val = ord(char)
            if char.islower():
                encrypted_str += chr((ascii_val - 97 + shift) % 26 + 97)
            else:
                encrypted_str += chr((ascii_val - 65 + shift) % 26 + 65)
        else:
            encrypted_str += char
    return encrypted_str
