from functools import cmp_to_key

def compare(a: str, b: str) -> int:
    n = len(a)

    for i in range(n):
        if i % 2 == 0:
            if a[i] != b[i]:
                return -(a[i] > b[i])  # compare in descending order
        else:
            if a[i] != b[i]:
                return a[i] > b[i]  # compare in ascending order

    return 0

def sort_asc_desc_ending(n: int, m: int, books: List[str]) -> List[int]:
    sorted_indices = sorted(range(n), key=cmp_to_key(lambda i, j: compare(books[i], books[j])))
    sorted_indices = [i+1 for i in sorted_indices]
    return sorted_indices
