def count_occurrences(n, m, a, b):
    occurrences = [0] * (n - m + 2)
    for k in range(n - m + 2):
        changes = 0
        for i in range(k, k + m):
            if a[i] != b[i - k]:
                changes += 1
        for i in range(k + m, n):
            if a[i] != a[i - m]:
                changes -= 1
        occurrences[k] = abs(changes)
    return occurrences


