def minimum_changes(n, m, a, b):
    occurrences = []
    for k in range(n - m + 2):
        count = 0
        for i in range(k, n - m + 1):
            flag = True
            for j in range(m):
                if a[i + j] != b[j]:
                    count += 1
                if count > k:
                    flag = False
                    break
            if flag:
                count = k
        if count != k:
            occurrences.append(-1)
        else:
            occurrences.append(count)
    return occurrences
