# Plan:
# 1. Generate all permutations of length n
# 2. For each permutation, calculate the number of different maximums in all subsegments containing each integer x
# 3. Count the number of permutations that have exactly k different maximums
# 4. Return the count modulo p

import itertools

def count_permutations(n, m, k, p):
    permutations = list(itertools.permutations(range(1, n+1)))
    count = 0

    for perm in permutations:
        max_counts = [0] * (n+1) # array to store the number of different maximums for each integer x

        for x in range(1, n+1):
            max_in_subsegments = []

            for i in range(n):
                for j in range(i, n):
                    subsegment = perm[i:j+1]
                    max_in_subsegment = max(subsegment)
                    if max_in_subsegment == x:
                        max_in_subsegments.append(max_in_subsegment)

            max_counts[x] = len(set(max_in_subsegments))

        if max_counts.count(m) == k:
            count += 1

    return count % p
