import itertools

def minimal_difference(a, b, c):
    total = a + 2 * b + 3 * c
    half_total = total // 2
    options = set()

    for i in range(a+1):
        for j in range(b+1):
            for k in range(c+1):
                duration = i + 2 * j + 3 * k
                if duration <= half_total:
                    options.add(duration)

    return half_total - max(options)
