def minimal_difference(a, b, c):
    total_duration = a + b + c

    half_duration = total_duration // 2

    possible_durations = set()
    for i in range(a + 1):
        for j in range(b + 1):
            for k in range(c + 1):
                possible_durations.add(i + 2 * j + 3 * k)

    min_difference = float('inf')
    for duration in possible_durations:
        difference = abs(duration - half_duration)
        if difference < min_difference:
            min_difference = difference

    return min_difference
