def find_xor_values(arr):
    from itertools import combinations
    n = len(arr)
    xor_set = set()
    for r in range(1, n+1):
        for comb in combinations(arr, r):
            xor = 0
            for num in comb:
                xor ^= num
            xor_set.add(xor)
    return sorted(list(xor_set))
