def winnerGame(A):
    n = len(A)
    dp = [[0] * n for _ in range(n)]

    for i in range(n):
        dp[i][i] = 1

    for l in range(2, n + 1):
        for i in range(n - l + 1):
            j = i + l - 1
            if A[i] < A[i+1]:
                dp[i][j] = dp[i+1][j]
            elif A[j] > A[j-1]:
                dp[i][j] = dp[i][j-1]
            else:
                dp[i][j] = 0

        turn = (n - l) % 2
        if dp[0][n-1] != turn:
            return 'Alice'

    return 'Bob'
