def winner(arr):
    n = len(arr)
    dp = [[0] * n for _ in range(n)]

    for i in range(n):
        dp[i][i] = 1

    for length in range(2, n + 1):
        for i in range(n - length + 1):
            j = i + length - 1
            if length == 2:
                dp[i][j] = 1 if arr[i] < arr[j] else 2
            else:
                dp[i][j] = 1 if arr[i] < arr[j] and dp[i + 1][j - 1] == 2 else 2

    return "Alice" if dp[0][n - 1] == 1 else "Bob"
