def winner(A):
    n = len(A)
    dp = [[0] * n for _ in range(n)]

    for i in range(n):
        dp[i][i] = A[i]

    for gap in range(1, n):
        for i in range(n - gap):
            j = i + gap
            for k in range(i, j+1):
                if i == k:
                    left_val = 0
                else:
                    left_val = dp[i][k-1]
                if k == j:
                    right_val = 0
                else:
                    right_val = dp[k+1][j]

                if A[k] > left_val and A[k] > right_val:
                    dp[i][j] = max(dp[i][j], A[k])

    return "Alice" if dp[0][n-1] > 0 else "Bob"
