import numpy as np

def determine_winner(arr):
    n = len(arr)
    dp = np.zeros((n, n))
    for i in range(n):
        dp[i][i] = 1

    for k in range(1, n):
        for i in range(n-k):
            j = i + k
            if arr[i] < arr[j]:
                dp[i][j] = 2 - dp[i][j-1]
            else:
                dp[i][j] = 2 - dp[i+1][j]

    if dp[0][n-1] == 1:
        return 'Alice'
    else:
        return 'Bob'
