def winner(lst):
    alice_score = 0
    bob_score = 0

    while len(lst) > 0:
        if lst[0] < lst[-1]:
            alice_score += lst[0]
            lst = lst[1:]
        else:
            alice_score += lst[-1]
            lst = lst[:-1]

        if len(lst) == 0:
            break

        if lst[0] < lst[-1]:
            bob_score += lst[0]
            lst = lst[1:]
        else:
            bob_score += lst[-1]
            lst = lst[:-1]

    if alice_score > bob_score:
        return "Alice"
    else:
        return "Bob"
