def smallest_beautiful_coloring(arr):
    arr_len = len(arr)
    for d in range(1, max(arr)+1):
        coloring = []
        for i in range(arr_len):
            if arr[i] % d == 0:
                coloring.append('red')
            else:
                coloring.append('blue')
        if all(coloring[i] != coloring[i+1] for i in range(arr_len-1)):
            return d
    return 0
