def smallest_beautiful_d(array):
    for d in range(1, max(array) + 1):
        colors = []
        for num in array:
            if num % d == 0:
                colors.append('red')
            else:
                colors.append('blue')

        if any(colors[i] == colors[i + 1] for i in range(len(colors) - 1)):
            continue
        else:
            return d

    return 0
