def smallest_beautiful_d(arr):
    unique_colors = set()
    for i in range(1, max(arr) + 1):
        colors = [1 if num % i == 0 else 0 for num in arr]
        if any(colors[j] == colors[j+1] for j in range(len(colors)-1)):
            continue
        else:
            unique_colors.add(i)
    if unique_colors:
        return min(unique_colors)
    else:
        return 0
