def find_smallest_d(arr):
    pairs = [(arr[i], arr[i+1]) for i in range(len(arr)-1)]
    for d in range(1, max(arr)+1):
        colors = [None] * len(arr)
        colors[0] = "red" if arr[0] % d == 0 else "blue"
        for i, (a, b) in enumerate(pairs):
            if arr[i+1] % d == 0:
                colors[i+1] = "red" if colors[i] == "blue" else "blue"
