def find_smallest_value(array):
    for d in range(1, max(array) + 1):
        colored_array = [0 for _ in range(len(array))]
        colored_array[0] = 1

        for i in range(1, len(array)):
            if array[i] % d == 0:
                colored_array[i] = 1 if colored_array[i-1] == 0 else -1
            else:
                colored_array[i] = -1 if colored_array[i-1] == 0 else 1

        if 0 not in colored_array:
            return d

    return 0
