def find_smallest_d(arr):
    for d in range(1, max(arr)+1):
        curr_color = None
        for num in arr:
            if num % d == 0:
                if curr_color == 'red':
                    break
                curr_color = 'red'
            else:
                if curr_color == 'blue':
                    break
                curr_color = 'blue'
        else:
            return d
    return 0
