def beautiful_coloring(array):
    for d in range(1, max(array) + 1):
        red_set = set()
        blue_set = set()
        for num in array:
            if num % d == 0:
                red_set.add(num)
            else:
                blue_set.add(num)

        if red_set and blue_set and max(red_set) < min(blue_set):
            return d

    return 0
