def find_smallest_d(arr):
    arr.sort()
    for d in range(1, arr[-1]+1):
        painted = []
        for num in arr:
            if num % d == 0:
                painted.append('red')
            else:
                painted.append('blue')
        if all(painted[i] != painted[i+1] for i in range(len(painted)-1)):
            return d
    return 0
