import itertools

def generate_ba_strings(k, s):
    asterisk_count = s.count('*')
    for combination in itertools.product(range(k+1), repeat=asterisk_count):
        ba_string = ''
        idx = 0
        for char in s:
            if char == '*':
                ba_string += 'b' * combination[idx]
                idx += 1
            else:
                ba_string += char
        yield ba_string

def find_xth_lexicographically_smallest(k, x, s):
    ba_strings = sorted(generate_ba_strings(k, s))
    return ba_strings[x-1]

find_xth_lexicographically_smallest(4, 3, 'a*')
