import re

def is_valid_date(date_string):
    regex = r'^(0[1-9]|1[0-2])-(0[1-9]|[12]\d|3[01])-\d{4}$'
    match = re.match(regex, date_string)
    if match:
        month = int(match.group(1))
        day = int(match.group(2))
        if month in [1, 3, 5, 7, 8, 10, 12]:
            return day <= 31
        elif month in [4, 6, 9, 11]:
            return day <= 30
        elif month == 2:
            year = int(match.group(3))
            if year % 400 == 0:
                return day <= 29
            elif year % 100 == 0:
                return day <= 28
            elif year % 4 == 0:
                return day <= 29
            else:
                return day <= 28
    else:
        return False
