import re

def find_intersection(interval1, interval2):
    start1, end1 = re.findall(r'\d+', interval1)
    start2, end2 = re.findall(r'\d+', interval2)

    start = max(int(start1), int(start2))
    end = min(int(end1), int(end2))

    if start <= end:
        return (start, end)
    else:
        return start

print(find_intersection('(1, 3)', '(2, 4)'))
print(find_intersection('(1, 2)', '(2, 3)'))
