def convert_base(num, base):
    return str(int(num, 10))[2:] if base == '2' else base_converter(int(num, 10), int(base))

def base_converter(num, base):
    result = ''
    while num >= base:
        remainder = num % base
        result += str(remainder)
        num = num // base
    result += str(num)
    return result[::-1]
