def deepest_nested_level(string):
    max_depth = 0
    current_depth = 0

    for char in string:
        if char == '(':
            current_depth += 1
            if current_depth > max_depth:
                max_depth = current_depth
        elif char == ')':
            current_depth -= 1

    return max_depth

print(deepest_nested_level("()"))
print(deepest_nested_level("((())()()"))
