import datetime

def parse_iso8601(date_str, delimiter='T', timezone=None):
    try:
        if timezone:
            dt = datetime.datetime.strptime(date_str, f"%Y-%m-%d{delimiter}%H:%M:%S%z")
        else:
            dt = datetime.datetime.strptime(date_str, f"%Y-%m-%d{delimiter}%H:%M:%S")
        return str(int(dt.timestamp()))
    except:
        return None
