def sort_asc_desc_ending(n, m, bookshelf):
    def key_func(book):
        odd_chars = sorted(book[::2])
        even_chars = sorted(book[1::2], reverse=True)
        return ''.join(odd_chars + even_chars)
    
    sorted_books = sorted(enumerate(bookshelf, start=1), key=lambda x: key_func(x[1]))
    return [book[0] for book in sorted_books]
