import itertools

def count_permutations(n, m, k, p):
    count = 0
    for perm in itertools.permutations(range(1, n+1)):
        max_count = 0
        for i in range(n):
            for j in range(i, n):
                subsegment = perm[i:j+1]
                if max(subsegment) == m:
                    max_count +=1
        if max_count == k:
            count += 1
    return count % p
