def minimal_difference(a, b, c):
    total_duration = a*1 + b*2 + c*3

    half_duration = total_duration // 2

    dp = [[False] * (half_duration+1) for _ in range(2)]

    dp[0][0] = True

    for i in range(a+1):
        for j in range(b+1):
            for k in range(c+1):
                if i*1 + j*2 + k*3 <= half_duration:
                    dp[1][i*1 + j*2 + k*3] = dp[0][i*1 + j*2 + k*3] or dp[0][i*1 + j*2 + k*3]

    diff = total_duration - 2 * half_duration

    return diff
