def min_concert_difference(a, b, c):
    total_min = a + b + c

    # Check all possible combinations of songs in first concert
    min_diff = float('inf')
    for i in range(a+1):
        for j in range(b+1):
            for k in range(c+1):
                first_concert_min = i + 2*j + 3*k
                second_concert_min = total_min - first_concert_min
                min_diff = min(min_diff, abs(first_concert_min - second_concert_min))

    return min_diff
