def get_min_difference(a, b, c):
    songs = [1] * a + [2] * b + [3] * c
    num_songs = len(songs)
    target_duration = sum(songs) // 2
    dp = [[False] * (target_duration + 1) for _ in range(num_songs + 1)]
    dp[0][0] = True

    for i in range(1, num_songs + 1):
        for j in range(target_duration + 1):
            if j >= songs[i - 1]:
                dp[i][j] = dp[i - 1][j] or dp[i - 1][j - songs[i - 1]]
            else:
                dp[i][j] = dp[i - 1][j]

    min_difference = float('inf')
    for j in range(target_duration + 1):
        if dp[num_songs][j]:
            min_difference = min(min_difference, abs(2 * j - sum(songs)))

    return min_difference
